// COLORS
$color-blue: rgb(81, 117, 255);
$color-light-blue: rgb(130, 156, 255);
$very-light-blue: rgb(180, 196, 255);
$color-dark-blue: rgb(51, 57, 80);
$color-very-dark-blue: rgb(37, 41, 58);
$color-grey: rgb(116, 123, 149);
$color-light-red: rgb(240, 91, 91);
$color-white: rgb(255, 255, 255);
$color-almost-white: rgb(250, 250, 250);

// TYPOGRAPHY
// font-size
$fs-sm-h1: 2.5rem;
$fs-lg-h1: 3.5rem;
$fs-md-p: 1rem;
$fs-lg-p: 1.125rem;
$fs-md: 1.25rem;
$fs-md: 2rem;
$fs-sm: 0.75rem;

// font-weight
$fw-bold: 700;
$fw-medium: 400;

// line-height
$lh-sm-h1: 120%; //64px
$lh-lg-h1: 114%; //48px //lh-price
$lh-md-p: 162%; //26px
$lh-lg-p: 156%; //28px
$lh-md: 140%; //28px
$lh-lg: 300%; //48px
$lh-count: 86%; //48px
$lh-count-sm: 150%; //48px
$lh-sm: 233%; //28px units

// letter-spacing
$ls-sm: 0.3125em;

