@use "../settings/variables" as *;
@use "../tools/functions" as *;

// MEDIA QUERY MANAGER
@mixin responsive($breakpoint) {
  @if $breakpoint == tablet {
    @media only screen and (min-width: rem(745px)) {
      @content;
    }
  }

  @if $breakpoint == desktop {
    @media only screen and (min-width: rem(978px)) {
      @content;
    }
  }

  @if $breakpoint == desktop-md {
    @media only screen and (min-width: rem(1230px)) {
      @content;
    }
  }
}

