@use "../settings/variables" as *;
@use "../tools/mixins" as *;
@use "../tools/functions" as *;

.btn {
  &,
  &:link,
  &:visited {
    font-size: $fs-md-p;
    font-weight: $fw-bold;
    line-height: $lh-lg-p;
    color: $color-white;
    padding: 0.875em 2.5625em;
    background: $color-blue;
    box-shadow: 0 1.5625rem 1.5625rem -0.625rem rgba(63, 91, 194, 0.247159);
    border-radius: 1.75rem;
    text-wrap: nowrap;
    transition: all 0.7s;
  }

  &:hover,
  &:active {
    background: $very-light-blue;
  }

  &:focus-visible {
    outline: rem(2px) double $color-dark-blue;
    outline-offset: rem(2px);
  }

  &--light {
    &,
    &:link,
    &:visited {
      color: $color-blue;
      background: $very-light-blue;
    }

    &:hover,
    &:active {
      mix-blend-mode: normal;
      opacity: 0.7;
    }
  }

  &--white {
    &,
    &:link,
    &:visited {
      color: $color-blue;
      background: $color-white;
    }

    &:hover,
    &:active {
      mix-blend-mode: normal;
      opacity: 0.7;
    }
  }

  &--submit {
    cursor: pointer;
    border: none;
    margin-top: 1rem;
    width: 100%;
  }
}

