    color: $color-blue;
  }
}

.cd-heading-signup {
  color: $color-dark-blue;
}

.countdown-numbers {
  color: $color-white;
  font-size: $fs-md-time;
  line-height: $lh-count-sm;
  font-weight: $fw-bold;

  @include responsive(tablet) {
    font-size: $fs-lg-h1;
    line-height: 86%;
  }
}

.numbers-blue {
  color: $color-blue;
}

.countdown-units {
  color: $color-white;
  font-size: $fs-sm;
  line-height: $lh-sm;
  font-weight: $fw-bold;
  mix-blend-mode: normal;
  opacity: 0.5;

  @include responsive(tablet) {
    font-size: $fs-md-p;
    line-height: 175%;
  }
}

.units--dark {
  color: $color-dark-blue;
}

